import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function ListaMedicamento({ medicamentos }) {
  const navigate = useNavigate();
  const [clickedId, setClickedId] = useState(null); // controla o card clicado

  const handleClick = (medicamento) => {
    setClickedId(medicamento.id);
    setTimeout(() => {
      navigate(`/detalhes/${medicamento.id}`, { state: { medicamento } });
    }, 300); // delay de 300ms
  };

  return (
    <div style={{ padding: 20, fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ textAlign: 'center', marginBottom: 20 }}>Medicamentos</h1>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        {medicamentos.map((medicamento) => (
          <div
            key={medicamento.id}
            onClick={() => handleClick(medicamento)}
            style={{
              cursor: 'pointer',
              display: 'flex',
              flexDirection: 'row',
              border: '1px solid #ddd',
              borderRadius: 15,
              boxShadow: '0 3px 15px rgba(0,0,0,0.15)',
              padding: 15,
              marginBottom: 20,
              maxWidth: 450,
              transition: 'transform 0.2s',
              transform: clickedId === medicamento.id ? 'scale(0.97)' : 'scale(1)',
            }}
            onMouseEnter={(e) => e.currentTarget.style.transform = clickedId === medicamento.id ? 'scale(0.97)' : 'scale(1.02)'}
            onMouseLeave={(e) => e.currentTarget.style.transform = clickedId === medicamento.id ? 'scale(0.97)' : 'scale(1)'}
          >
            <img src={medicamento.imageUrl} alt={medicamento.nome} style={{ width: 100, height: 100, borderRadius: 12, marginRight: 15 }} />
            <div style={{ flex: 1 }}>
              <h2 style={{ margin: 0 }}>{medicamento.nome}</h2>
              <p style={{ margin: '5px 0', color: '#555' }}>{medicamento.descricao}</p>
              <p style={{ fontWeight: 'bold', fontSize: 18 }}>R$ {medicamento.preco.toFixed(2)}</p>
              <div style={{ marginTop: 5 }}>
                {medicamento.maisPesquisado && <span style={{ backgroundColor: '#f39c12', color: '#fff', padding: '3px 8px', borderRadius: 5, fontSize: 12, marginRight: 5 }}>Mais Pesquisado</span>}
                {medicamento.topDoMomento && <span style={{ backgroundColor: '#27ae60', color: '#fff', padding: '3px 8px', borderRadius: 5, fontSize: 12 }}>Top do Momento</span>}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
