import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import DetalheMedicamento from './DetalheMedicamento';

const medicamentoTeste = {
  id: "1",
  nome: "Metformina 500mg",
  descricao: "Controle da glicemia em Diabetes Tipo 2",
  dosagem: "500mg",
  estoque: 50,
  forma: "Comprimido",
  funcao: "Controle glicêmico",
  categoria: "Diabetes",
  imageUrl: "https://santaluciadrogaria.vtexassets.com/arquivos/ids/187467/7891721000614.png?v=637704966349670000",
  preco: 15.9,
  maisPesquisado: true,
  topDoMomento: true
};

function ListaMedicamento() {
  return (
    <div style={{ padding: 20 }}>
      <h1>Medicamentos</h1>
      <div style={{ border: '1px solid #ccc', padding: 10, marginBottom: 10 }}>
        <Link to={`/detalhes/${medicamentoTeste.id}`} style={{ textDecoration: 'none', color: 'black' }}>
          <img src={medicamentoTeste.imageUrl} alt={medicamentoTeste.nome} width={60} />
          <div>
            <h2>{medicamentoTeste.nome}</h2>
            <p>{medicamentoTeste.descricao}</p>
            <p>R$ {medicamentoTeste.preco.toFixed(2)}</p>
            {medicamentoTeste.maisPesquisado && <span style={{ backgroundColor: '#f39c12', color: '#fff', padding: '2px 5px', marginRight: 5 }}>Mais Pesquisado</span>}
            {medicamentoTeste.topDoMomento && <span style={{ backgroundColor: '#27ae60', color: '#fff', padding: '2px 5px' }}>Top do Momento</span>}
          </div>
        </Link>
      </div>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<ListaMedicamento />} />
        <Route path="/detalhes/:id" element={<DetalheMedicamento medicamento={medicamentoTeste} />} />
      </Routes>
    </Router>
  );
}
