import React from 'react';
import { Link } from 'react-router-dom';

export default function DetalheMedicamento({ medicamento }) {
  return (
    <div style={{ padding: 20, fontFamily: 'Arial, sans-serif', maxWidth: 600, margin: '0 auto' }}>
      <Link to="/" style={{ textDecoration: 'none', color: '#2980b9', fontWeight: 'bold' }}>← Voltar</Link>
      <div style={{
        border: '1px solid #ddd',
        borderRadius: 15,
        boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
        padding: 20,
        marginTop: 15
      }}>
        <img src={medicamento.imageUrl} alt={medicamento.nome} style={{ width: '100%', borderRadius: 15, marginBottom: 20 }} />
        <h1 style={{ margin: '10px 0' }}>{medicamento.nome}</h1>
        <p style={{ fontWeight: 'bold', fontSize: 20 }}>R$ {medicamento.preco.toFixed(2)}</p>
        <p style={{ fontStyle: 'italic', color: '#555' }}>{medicamento.categoria}</p>

        <div style={{ marginTop: 15 }}>
          <p><strong>Função:</strong> {medicamento.funcao}</p>
          <p><strong>Dosagem:</strong> {medicamento.dosagem}</p>
          <p><strong>Descrição:</strong> {medicamento.descricao}</p>
          <p><strong>Estoque:</strong> {medicamento.estoque} unidades</p>
        </div>

        <div style={{ marginTop: 20 }}>
          {medicamento.maisPesquisado && <span style={{ backgroundColor: '#f39c12', color: '#fff', padding: '6px 12px', borderRadius: 6, marginRight: 5 }}>Mais Pesquisado</span>}
          {medicamento.topDoMomento && <span style={{ backgroundColor: '#27ae60', color: '#fff', padding: '6px 12px', borderRadius: 6 }}>Top do Momento</span>}
        </div>
      </div>
    </div>
  );
}
