import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import DetalheMedicamento from './DetalheMedicamento';

const medicamentoTeste = {
  id: "1",
  nome: "Metformina 500mg",
  descricao: "Controle da glicemia em Diabetes Tipo 2",
  dosagem: "500mg",
  estoque: 50,
  forma: "Comprimido",
  funcao: "Controle glicêmico",
  categoria: "Diabetes",
  imageUrl: "https://santaluciadrogaria.vtexassets.com/arquivos/ids/187467/7891721000614.png?v=637704966349670000",
  preco: 15.9,
  maisPesquisado: true,
  topDoMomento: true
};

function ListaMedicamento() {
  return (
    <div style={{ padding: 20, fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ textAlign: 'center', marginBottom: 20 }}>Medicamentos</h1>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <div
          style={{
            display: 'flex',
            flexDirection: 'row',
            border: '1px solid #ddd',
            borderRadius: 10,
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            padding: 15,
            marginBottom: 20,
            alignItems: 'center',
            width: '100%',
            maxWidth: 400
          }}
        >
          <img src={medicamentoTeste.imageUrl} alt={medicamentoTeste.nome} style={{ width: 80, height: 80, borderRadius: 10, marginRight: 15 }} />
          <div style={{ flex: 1 }}>
            <h2 style={{ margin: 0 }}>{medicamentoTeste.nome}</h2>
            <p style={{ margin: '5px 0', color: '#555' }}>{medicamentoTeste.descricao}</p>
            <p style={{ margin: '5px 0', fontWeight: 'bold', color: '#2c3e50' }}>R$ {medicamentoTeste.preco.toFixed(2)}</p>
            <div>
              {medicamentoTeste.maisPesquisado && <span style={{ backgroundColor: '#f39c12', color: '#fff', padding: '3px 6px', borderRadius: 5, marginRight: 5, fontSize: 12 }}>Mais Pesquisado</span>}
              {medicamentoTeste.topDoMomento && <span style={{ backgroundColor: '#27ae60', color: '#fff', padding: '3px 6px', borderRadius: 5, fontSize: 12 }}>Top do Momento</span>}
            </div>
            {/* Botão de detalhes */}
            <div style={{ marginTop: 10 }}>
              <Link to={`/detalhes/${medicamentoTeste.id}`} style={{ textDecoration: 'none' }}>
                <button style={{
                  padding: '8px 12px',
                  backgroundColor: '#2980b9',
                  color: '#fff',
                  border: 'none',
                  borderRadius: 5,
                  cursor: 'pointer'
                }}>Detalhes</button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<ListaMedicamento />} />
        <Route path="/detalhes/:id" element={<DetalheMedicamento medicamento={medicamentoTeste} />} />
      </Routes>
    </Router>
  );
}
