import React from 'react';
import { Link } from 'react-router-dom';

export default function DetalheMedicamento({ medicamento }) {
  return (
    <div style={{ padding: 20 }}>
      <Link to="/">← Voltar</Link>
      <h1>{medicamento.nome}</h1>
      <img src={medicamento.imageUrl} alt={medicamento.nome} width={200} />
      <p><strong>Preço:</strong> R$ {medicamento.preco.toFixed(2)}</p>
      <p><strong>Categoria:</strong> {medicamento.categoria}</p>
      <p><strong>Função:</strong> {medicamento.funcao}</p>
      <p><strong>Dosagem:</strong> {medicamento.dosagem}</p>
      <p><strong>Descrição:</strong> {medicamento.descricao}</p>
      <p><strong>Estoque:</strong> {medicamento.estoque} unidades</p>
      {medicamento.maisPesquisado && <span style={{ backgroundColor: '#f39c12', color: '#fff', padding: '2px 5px', marginRight: 5 }}>Mais Pesquisado</span>}
      {medicamento.topDoMomento && <span style={{ backgroundColor: '#27ae60', color: '#fff', padding: '2px 5px' }}>Top do Momento</span>}
    </div>
  );
}
